import React, { useState } from "react";
import axios from "axios";
import "./PlacementRegistrationForm.css"; // Import the external CSS file

const PlacementRegistrationForm = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    password: "",
    rePassword: "",
    gender: "",
    email: "",
    dob: "",
    mobile: "",
    address: "",
    branch: "CSE",
    tenth: "",
    twelfth: "",
    ug: "",
    plang: [],
    db: [],
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setFormData((prev) => ({
        ...prev,
        [name]: checked
          ? [...(prev[name] || []), value]
          : prev[name].filter((v) => v !== value),
      }));
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.rePassword) {
      alert("Passwords do not match!");
      return;
    }
    try {
      await axios.post("http://localhost:5000/register", formData);
      alert("Registration successful!");
    } catch (error) {
      alert("Error in submission: " + error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Placement Registration Form</h3>

      {/* Personal Information */}
      <fieldset>
        <legend>Personal Information</legend>
        <div className="form-group">
          <label>First Name:</label>
          <input type="text" name="firstName" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Last Name:</label>
          <input type="text" name="lastName" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input type="password" name="password" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Confirm Password:</label>
          <input type="password" name="rePassword" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Gender:</label>
          <input type="radio" name="gender" value="Male" onChange={handleChange} required /> Male
          <input type="radio" name="gender" value="Female" onChange={handleChange} required /> Female
        </div>
        <div className="form-group">
          <label>Email ID:</label>
          <input type="email" name="email" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>DOB:</label>
          <input type="date" name="dob" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Mobile:</label>
          <input type="text" name="mobile" onChange={handleChange} required pattern="[0-9]{10}" />
        </div>
        <div className="form-group">
          <label>Address:</label>
          <textarea name="address" rows="3" cols="30" onChange={handleChange} required></textarea>
        </div>
      </fieldset>

      {/* Academics */}
      
        <legend>Academics</legend>
        <div className="form-row">
          <label>Branch:</label>
          <select name="branch" onChange={handleChange} required>
            <option value="CSE">CSE</option>
            <option value="ECE">ECE</option>
            <option value="EEE">EEE</option>
            <option value="IT">IT</option>
            <option value="MECH">MECH</option>
          </select>
          <label>10th:</label>
          <input type="number" name="tenth" onChange={handleChange} required />
          <label>12th:</label>
          <input type="number" name="twelth" onChange={handleChange} required />
          <label>UG Grade:</label>
          <input type="number" name="ug" onChange={handleChange} required />
        </div>
     

      {/* Skills */}
      <fieldset>
        <legend>Skills</legend>
        <div className="skills">
          <label>Programming Languages:</label>
          <input type="checkbox" name="plang" value="C" onChange={handleChange} /> C
          <input type="checkbox" name="plang" value="C++" onChange={handleChange} /> C++
          <input type="checkbox" name="plang" value="Java" onChange={handleChange} /> Java
          <input type="checkbox" name="plang" value="C#" onChange={handleChange} /> C#
        </div>
        <div className="skills">
          <label>DBMS:</label>
          <input type="checkbox" name="db" value="Oracle" onChange={handleChange} /> Oracle
          <input type="checkbox" name="db" value="MySQL" onChange={handleChange} /> MySQL
          <input type="checkbox" name="db" value="DB2" onChange={handleChange} /> DB2
          <input type="checkbox" name="db" value="Others" onChange={handleChange} /> Others
        </div>
      </fieldset>

      <div style={{ textAlign: "center" }}>
        <input type="submit" value="Register" id="register" />
        <input type="reset" value="Clear" />
      </div>
    </form>
  );
};

export default PlacementRegistrationForm;
